import React from "react";

import { useState, useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, getActivitiesList, countriesTools } from "../actions";
import { Link } from "react-router-dom";
import CountryCard from "./CountryCard.jsx";
import Paging from "./Paging";
import style from "./Home.module.css";
import "./Global.css";

export default function Home() {
  const dispatch = useDispatch();

  // LOCAL STATES
  const [filter, setFilter] = useState({ region: "", activity: "" });
  const [order, setOrder] = useState('');
  const [searchText, setSearchText] = useState('');
  const [allCountries, setAllCountries] = useState([]);
  const [regionList, setRegionList] = useState([]);
  // GLOBAL STATE
  const countries = useSelector((state) => state.countries, () => { });
  const activitiesList = useSelector((state) => state.activitiesList);
  // DOM REFERENCES
  const inputSearch = useRef();
  // PAGING
  const [currentPage, setCurrentPage] = useState(1);
  const [countriesXPage] = useState(9);
  const [totalPages, setTotalPages] = useState(0);

  // Seria interesante hacer orderList dinámica
  // const orderList = ['none', 'asc', 'des', 'popUp', 'popDown'];

  /////// INITIALIZATION
  useEffect(() => {
    dispatch(getCountries());
    dispatch(getActivitiesList());
  }, [])
  // Cuando countries esta cargado:
  useEffect(() => {
    if (allCountries.length === 0) {  // Comprobación, para que esto suceda una sola vez.
      setAllCountries([...countries]);
      setRegionList(countries
        .map((obj) => obj.region) // convierte el obj en arr
        .filter((item, index, arr) => arr.indexOf(item) === index)); // filtra repeticiones);
      pages(1);
    }
  }, [countries])

  // Re-inicialización forzada con botón
  function handleReloadDB(e) {
    e.preventDefault();
    setAllCountries([]);
    dispatch(getCountries());
    dispatch(getActivitiesList());
  }

  ///// PAGING

  const lastCountry = currentPage * countriesXPage;
  const firstCountry = lastCountry - countriesXPage;
  const pageCountries = countries.slice(firstCountry, lastCountry);

  const pages = (page) => {
    // if (page === 1) setCountriesXPage(9);
    // else setCountriesXPage(10);
    setCurrentPage(page);
  };

  useEffect(() => {
    if (countries) {
      const calcTotalPages = Math.ceil(countries.length / countriesXPage)
      setTotalPages(calcTotalPages);
      if (currentPage > calcTotalPages) setCurrentPage(1);
    }
  }, [countries]);
  //////////////////////////// COUNTRIES TOOLS : FILTER, SEARCH && ORDER

  useEffect(() => {
    dispatch(countriesTools(filter, searchText, order, allCountries));
    pages(currentPage);
  }, [order, filter, searchText]);

  // FILTER
  function handleFilter(e) {
    setFilter({ ...filter, [e.target.name]: e.target.value }); // { region:'Asia', activity:'Cagar' }
  }
  // SEARCH
  function handleResetButton(e) {
    e.preventDefault();
    setSearchText('');
    inputSearch.current.value = '';
  }
  function handleSearchText(e) {
    setSearchText(e.target.value);
  }
  // ORDER
  function handleOrder(e) {
    setOrder(e.target.id);
  }

  // La barra de botones se podria modularizar
  return (
    <div id={style.container}>

      <div id={style.createAct}>
        {/* LINK TO FORM */}
        <Link to="/activities">Crear actividad</Link>
        <h1>Home de Countries</h1>
        <button onClick={(e) => { handleReloadDB(e) }}>reload DB</button>
      </div>

      <div id={style.order}>
        {/* SELECT ORDER */}
        {/* <input type="radio" id="none" name="order" value="NONE" onChange={handleOrder} />
        <label>None</label> */}
        <input type="button" id="asc" name="order" value="ASC" onChange={handleOrder} />
        <label>Asc</label>
        <input type="button" id="des" name="order" value="DES" onChange={handleOrder} />
        <label>Des</label>
        <input type="button" id="popUp" name="order" value="PopUp" onChange={handleOrder} />
        <label>PopUp</label>
        <input type="button" id="popDown" name="order" value="PopDown" onChange={handleOrder} />
        <label>PopDown</label>
      </div>

      {/* SEARCH TEXT */}
      <div id={style.search}>
        <label>Search:</label>
        <input type="text" id="searchText" name="searchText" onChange={handleSearchText} ref={inputSearch} />
        <input type="button" onClick={handleResetButton} value="Reset" />
      </div>

      <div id={style.filter}>
        {/* SELECT REGION */}
        <select name="region" id="" onChange={handleFilter}>
          <option value='all' key='all' >* ALL *</option>
          {
            regionList && regionList.map((reg) => {
              return (<option value={reg} key={reg} >{reg}</option>);
            })}
        </select>

        {/* SELECT ACTIVITY  */}
        <select name="activity" id="" onChange={handleFilter}>
          <option value='all' key='all' >* ALL *</option>
          {
            activitiesList && activitiesList.map((act) => {
              return (<option value={act} key={act} >{act}</option>);
            })}
        </select>
      </div>

      {/* PAGING  */}
      <Paging
        totalPages={totalPages}
        currentPage={currentPage}
        pages={pages}
      />

      {/* COUNTRY CARDS  */}
      <div id={style.countries}>
        {pageCountries ?
          (pageCountries.map((country) => {
            return (
              <CountryCard
                key={country.id}
                name={country.name}
                region={country.region}
                flag={country.flag}
                flag_icon={country.flag_icon}
                id={country.id}
              />
            );
          }))
          : (<h2>No hay paises para mostrar</h2>)}
      </div>

    </div>
  );
}
