import React from "react";
import './Paging.css'

export default function Paging({ countriesXPage, totalCountries, pages }) {
  const pageNumbers = [];
  const totalPages = Math.ceil(totalCountries / countriesXPage);
  console.log("ðŸš€ ~ file: Paging.jsx ~ line 7 ~ Paging ~ totalPages", totalPages)

  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }

  return (
    <nav>
      <ul>
        {pageNumbers &&
          pageNumbers.map((pageNumber) => (
            <li key={pageNumber}>
              <a onClick={() => pages(pageNumber)}>{pageNumber}</a>
            </li>
          ))}
      </ul>
    </nav>
  );
}
