import React from "react";
import './Paging.css'

export default function Paging({ totalPages, currentPage,pages }) {
  const   pageNumbers = [];


  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }
  const firstPage = 1;
  const lastPage = totalPages;

  return (
    <nav>
      <ul>
            {/* Prev */}
            {
              (currentPage !== 1) && 
              <li><button onClick={() => pages(currentPage-1)}>Prev</button></li>
            }
            {/* First number */}
            {
              (currentPage !== 1) && 
              <li><button onClick={() => pages(1)}>First</button></li>
            }
            {/* ...  */}
            {
              (currentPage !== 1) && 
              <li>...</li>
            }
            {/* previous number */}
            {
              (currentPage !== 1) && 
              <li>
                <a onClick={() => pages(currentPage-1)} id='pageNumber'>
                  {currentPage-1}
                </a>
              </li>
            }
            {/* current */}
            <li><button onClick={() => pages(currentPage)}>{currentPage}</button></li>

            {/* next number */}
            {
              (currentPage !== lastPage) && 
              <li><button onClick={() => pages(currentPage+1)}>{currentPage+1}</button></li>
            }
            {/* ...  */}
            {
              (currentPage !== lastPage) && 
               <li>...</li>
            }

            {/* Last number */}
            {
              (currentPage !== lastPage) &&
               <li><button onClick={() => pages(pageNumbers.length)}>Last</button></li>
            }
            {/* next */}
            {
              (currentPage !== lastPage) &&
              <li><button onClick={() => pages(currentPage+1)}>Next</button></li>
            }

      </ul>
    </nav>
  );
}
