import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, getActivitiesList, orderCountries } from "../actions";
import { Link } from "react-router-dom";
import CountryCard from "./CountryCard.jsx";

export default function Home() {
  const dispatch = useDispatch();
  const countries = useSelector((state) => state.countries);
  const activitiesList = useSelector((state) => state.activitiesList);


  useEffect(() => {
    // dispatch(getCountries());
    dispatch(getActivitiesList());
    return () => {};
  }, [dispatch]);

  function handleReloadDB(e) {
    e.preventDefault();
    dispatch(getCountries());
  }

  function handleOrder(e) {
    const order = e.target.id;   
    console.log(" ~ file: Home.jsx ~ line 26 ~ handleOrder ~ order", order)
    dispatch(orderCountries(order,countries));  // order=id del bot贸n: discrimina la acci贸n
  }

  function getRegionsList() {
    return countries
      .map((obj) => obj.region) // convierte el obj en arr
      .filter((item, index, arr) => arr.indexOf(item) === index); // filtra repeticiones
  }

  // La barra de botones se podria modularizar
  return (
    <div>
      <Link to="/activity">Crear actividad</Link>
      <h1>Home de Countries</h1>
      <button
        onClick={(e) => {
          handleReloadDB(e);
        }}
      >
        reload DB
      </button>
      <div>
        {/* <select name="orderBy" id="">
          <option value="asc">Ascendente</option>
          <option value="des">Descendente</option>
          <option value="des">Poblaci贸n</option>
        </select> */}
        <div>
          <input type="radio" id="asc" name="order" value="ASC" onChange={handleOrder}/>
          <label for="asc">Asc</label>
          <input type="radio" id="des" name="order" value="DES" onChange={handleOrder} />
          <label for="asc">Des</label>
          <input type="radio" id="pop" name="order" value="Pop" onChange={handleOrder} />
          <label for="asc">Pop</label>
        </div>

        <label>Search:</label>
        <input type="text" id="search" name="search"></input>
        <button>Serch</button>

        <select name="region">
          {countries ? (
            getRegionsList().map((reg) => {
              return (
                <option value={reg} key={reg}>
                  {reg}
                </option>
              );
            })
          ) : (
            <h2>No hay regiones para mostrar</h2>
          )}
        </select>

        <select name="activity" id="">
          {activitiesList &&
            activitiesList.map((act) => {
              return (
                <option value={act} key={act}>
                  {act}
                </option>
              );
            })}
        </select>

        <div>
          {countries ? (
            countries.map((country) => {
              return (
                <CountryCard
                  key={country.id}
                  name={country.name}
                  region={country.region}
                  flag={country.flag}
                />
              );
            })
          ) : (
            <h2>No hay paises para mostrar</h2>
          )}
        </div>
      </div>
    </div>
  );
}
