        }
    }, [countries]);

    return (
        <div><ul>
            {
                selectedCountries &&
                selectedCountries.map(c =>

                (<li key={c.id}>
                    <input type={checkbox} />
                    {c.flag_icon}
                    {c.name}
                </li>)
                )
            }

        </ul></div>
    )
}
