import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getCountryDetails } from "../actions";
import ActivityCard from "./ActivityCard";
import './CountryDetails.css';

export default function CountryDetails() {
    const dispatch = useDispatch();
    const { id } = useParams();

    const countryDetails = useSelector((state) => state.countryDetails);

    // Inicialización
    useEffect(() => {
        dispatch(getCountryDetails(id));
    }, [])

    return (
        <div>
            {
                countryDetails ?
                    (<div id='countryDetail'>
                        {countryDetails.flag_icon}
                        <h4>{countryDetails.name}</h4>
                        <h3>{countryDetails.id}</h3>
                        <img src={countryDetails.flag} alt="flag not found" width="150px" />
                        <h4>Región : {countryDetails.region}-{countryDetails.subregion}</h4>
                        <h4>Capital: {countryDetails.capital}</h4>
                        <h4>Área: {countryDetails.area}</h4>
                        <h4>Población: {countryDetails.population}</h4>
                        {
                            countryDetails.activities &&
                            countryDetails.activities.map(act => (
                                <ActivityCard
                                    name={act.name}
                                    difficulty={act.difficulty}
                                    duration={act.duration}
                                    season={act.season}
                                />
                            ))
                        }
                    </div>
                    ) : <h2>Cagando...</h2>
            }
        </div>
    )
}
