import React from "react";
import './Paging.css'

export default function Paging({ totalPages, currentPage,pages }) {
  const   pageNumbers = [];

  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }
  const firstPage = 1;
  const lastPage = totalPages;

  return (
    <nav>
      <ul>
        {/* Prev */}
        <li visibility={currentPage !== firstPage ? 'visible' : 'hidden'}><button onClick={() => pages(currentPage-1)}>Prev</button></li>

        {/* First number */}
        {(currentPage !== firstPage) && 
          <li><button onClick={() => pages(firstPage)}>First</button></li>}

        {/* ...  */}
        {(currentPage !== firstPage) && <li>...</li>}

        {/* previous number */}
        {(currentPage !== firstPage) && 
          <li><button onClick={() => pages(currentPage-1)}>{currentPage-1}</button></li>}

        {/* current */}
        <li><button id='current'>{currentPage}</button></li>

        {/* next number */}
        {(currentPage !== lastPage) && 
          <li><button onClick={() => pages(currentPage+1)}>{currentPage+1}</button></li>}

        {/* ...  */}
        {(currentPage !== lastPage) && <li>...</li>}

        {/* Last number */}
        {(currentPage !== lastPage) &&
            <li><button onClick={() => pages(lastPage)}>Last</button></li>}

        {/* next */}
        {(currentPage !== lastPage) &&
          <li><button onClick={() => pages(currentPage+1)}>Next</button></li>}
      </ul>
    </nav>
  );
}
