import React from "react";

import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, getActivitiesList, filterOrderCountries, searchCountry } from "../actions";
import { Link } from "react-router-dom";
import CountryCard from "./CountryCard.jsx";
import Pages from "./Pages";
import "./Home.css";

export default function Home() {
  const dispatch = useDispatch();

  // LOCAL STATES
  const [filter, setFilter] = useState({ region: "", activity: "" });
  const [order, setOrder] = useState('');
  const [searchText, setSearchText] = useState('');
  const [allCountries, setAllCountries] = useState([]);
  const [regionList, setRegionList] = useState([]);

  const countries = useSelector((state) => state.countries, () => { });
  const activitiesList = useSelector((state) => state.activitiesList);

  // Paginado
  const [currentPage, setCurrentPage] = useState(1);
  const [countriesXPage, setCountriesXPage] = useState(9);
  const lastCountry = currentPage * countriesXPage;
  const firstCountry = lastCountry - countriesXPage;
  const pageCountries = countries.slice(firstCountry, lastCountry);

  const pages = (page) => {
    setCurrentPage(page);
    if (page === 1) setCountriesXPage(9);
    else setCountriesXPage(10);
  };

  ////////////////////////////// InicializaciÃ³n
  useEffect(() => {
    dispatch(getCountries());
    dispatch(getActivitiesList());
  }, [])
  // Llena allCountries una sola vez, cuando countries esta cargado
  useEffect(() => {
    if (allCountries.length === 0) {
      setAllCountries([...countries]);
      setRegionList(getRegionsList());
    }
  }, [countries])

  // InicializaciÃ³n forzada con botÃ³n
  function handleReloadDB(e) {
    e.preventDefault();
    setAllCountries([]);
    dispatch(getCountries());
    dispatch(getActivitiesList());
  }
  /////////////////////////////////////////////////////////////

  function getRegionsList() {
    return countries
      .map((obj) => obj.region) // convierte el obj en arr
      .filter((item, index, arr) => arr.indexOf(item) === index); // filtra repeticiones
  }

  //////////////////////////// ORDER && FILTER

  useEffect(() => {
    dispatch(filterOrderCountries(filter,order, allCountries));
  }, [order, filter]);

  // FILTER
  function handleFilter(e) {
    setFilter({ ...filter, [e.target.name]: e.target.value }); // { region:'Asia', activity:'Cagar' }
  }
  // ORDER
  function handleOrder(e) {
    setOrder(e.target.id);
  }

  //////////////////////////// SEARCH
  function handleSearchButton(e) {
    console.log("ðŸš€ ~ file: Home.jsx ~ line 82 ~ handleSearchButton ~ e", e)
    e.preventDefault();
    dispatch(searchCountry(searchText));
  }
  function handleSearchText(e) {
    setSearchText(e.target.value);
  }

  // La barra de botones se podria modularizar
  return (
    <div>
      <Link to="/activity">Crear actividad</Link>
      <h1>Home de Countries</h1>
      <button onClick={(e) => { handleReloadDB(e) }}>reload DB</button>
      <div>
        <div>
          <input type="radio" id="asc" name="order" value="ASC" onChange={handleOrder} />
          <label>Asc</label>
          <input type="radio" id="des" name="order" value="DES" onChange={handleOrder} />
          <label>Des</label>
          <input type="radio" id="popUp" name="order" value="PopUp" onChange={handleOrder} />
          <label>PopUp</label>
          <input type="radio" id="popDown" name="order" value="PopDown" onChange={handleOrder} />
          <label>PopDown</label>
        </div>


        <label>Search:</label>
        <input type="text" id="searchText" name="searchText" onChange={handleSearchText}></input>
        <input type="button" onClick={handleSearchButton} value="Search" />


        {/* SELECT REGION */}
        <select name="region" id="" onChange={handleFilter}>
          <option value='all' key='all' >* ALL *</option>
          {
            regionList && regionList.map((reg) => {
              return (<option value={reg} key={reg} >{reg}</option>);
            })}
        </select>

        {/* SELECT ACTIVITY  */}
        <select name="activity" id="" onChange={handleFilter}>
          <option value='all' key='all' >* ALL *</option>
          {
            activitiesList && activitiesList.map((act) => {
              return (<option value={act} key={act} >{act}</option>);
            })}
        </select>

        <Pages
          countriesXPage={countriesXPage}
          totalCountries={countries.length}
          pages={pages}
        />

        <div id="countries">
          {pageCountries ?
            (pageCountries.map((country) => {
              return (
                <CountryCard
                  key={country.id}
                  name={country.name}
                  region={country.region}
                  flag={country.flag}
                  id={country.id}
                />
              );
            }))
            : (<h2>No hay paises para mostrar</h2>)}
        </div>
      </div>
    </div>
  );
}
