import React from "react";

const initialState = {
    countries: []
}

function rootReducer(state = initialState, { type, payload }) {
    switch (type) {
        case 'GET_COUNTRIES':
            return ({ ...state, countries: payload })
        default: return state;
    }
}


export default rootReducer;
