import React from "react";
import './Paging.css'

export default function Pages({ countriesXPage, totalCountries, pages }) {
  let pageNumbers = [];
  const totalPages = Math.ceil(totalCountries / countriesXPage);

  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }
  //////////
  const totPages = pageNumbers.length;
  if (totPages > 15) {
    const iniSlice = Math.round((totPages/2)-(15/2));
    const endSlice = Math.round((totPages/2)+(15/2));
    pageNumbers = pageNumbers.slice(iniSlice,endSlice);
    pageNumbers = pageNumbers.splice(iniSlice,0,'...');
  }
  return (
    <nav>
      <ul>
        {pageNumbers &&
          pageNumbers.map((pageNumber) => (
            <li key={pageNumber}>
              <a onClick={() => pages(pageNumber)}>{pageNumber}</a>
            </li>
          ))}
      </ul>
    </nav>
  );
}
