import React, { useState } from 'react';

export function validate(input) {
  let errors = {};
  if (!input.username) {
      errors.username = 'Username is required';
  } else if (!/\S+@\S+\.\S+/.test(input.username)) {
      errors.username = 'Username is invalid';
  }
  if (!input.password || input.password === '') {
      errors.password = 'Password is required'
  } else if(!/(?=.*[0-9])/.test(input.password)) {
      errors.password = 'Password is invalid'
  }
  // console.log(errors);
  return errors;
};

function Activities() {

  const [input, setInput] = useState({
    name: '',
    difficulty: '',
    duration: '',
    season: '',
  });
  const [errors, setErrors] = useState({});

  function handleInputChange(e) {
    const currentInputs = {...input,[e.target.name]: e.target.value,};
    setInput(currentInputs);
    setErrors(validate(currentInputs));
    // setInput({
    //   ...input,                           // Las propiedades que ya tenia input
    //   [e.target.name]: e.target.value,}); // + las nueva. Si ya existe, la pisa.
    // setErrors(validate({
    //   ...input,
    //   [e.target.name]: e.target.value,
    // }))
  }

  return (
    <div>
      <form>
        <div>
          <label>Activity:</label>
          <input type="text" name="username" value={input.username}
            className={errors.username && 'danger'} // errors.username ? 'danger' : ''
            onChange={handleInputChange} />
          {/* onChange le pasa el evento a la funcion que le pasemos, por eso no hay que pasarselo explicitamente */}
          {errors.username && (
            <p className='danger'>{errors.username}</p>
          )}
        </div>
        <div>
          <label>Difficulty:</label>
          <input type="text" name="username" value={input.username}
            className={errors.username && 'danger'} // errors.username ? 'danger' : ''
            onChange={handleInputChange} />
          {/* onChange le pasa el evento a la funcion que le pasemos, por eso no hay que pasarselo explicitamente */}
          {errors.username && (
            <p className='danger'>{errors.username}</p>
          )}
        </div>
        <div>
          <label>Duration:</label>
          <input type="text" name="username" value={input.username}
            className={errors.username && 'danger'} // errors.username ? 'danger' : ''
            onChange={handleInputChange} />
          {/* onChange le pasa el evento a la funcion que le pasemos, por eso no hay que pasarselo explicitamente */}
          {errors.username && (
            <p className='danger'>{errors.username}</p>
          )}
        </div>
        <div>
          <label>Season:</label>
          <input type="text" name="username" value={input.username}
            className={errors.username && 'danger'} // errors.username ? 'danger' : ''
            onChange={handleInputChange} />
          {/* onChange le pasa el evento a la funcion que le pasemos, por eso no hay que pasarselo explicitamente */}
          {errors.username && (
            <p className='danger'>{errors.username}</p>
          )}
        </div>



        <input type="submit" value="Submit" disabled = {Object.keys(errors).length}/>
      </form>
    </div>
  )
}

export default Activities;
