import React from 'react'

export default function Nav() {
    return (
        <div>
            <label>Results/page:</label>
            <input type="range" min='8' max='36' value={countriesXPage} onChange={(e) => setCountriesXPage(e.target.value)} />
            <span>{countriesXPage}</span>
            {/* LINK TO ACTIVITIES FORM */}
            <Link to="/activities">Crear actividad</Link>
            {/* Reload DB */}
            <input type="button" value="Reload" onChange={handleReloadDB} />
        </div>
    )
}
