import React from "react";
import './Paging.css'

export default function Paging({ countriesXPage, totalCountries, pages, currentPage }) {
  const   pageNumbers = [];
  const totalPages = Math.ceil(totalCountries / countriesXPage);

  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }
  const firstPage = 1;
  const lastPage = pageNumbers.length;
  console.log("ðŸš€ ~ file: Paging.jsx ~ line 13 ~ Paging ~ lastPage", lastPage)

  return (
    <nav>
      <ul>
            {/* Prev */}
            {
              (currentPage !== 1) && 
              <li><a onClick={() => pages(currentPage-1)}>Prev</a></li>
            }
            {/* First number */}
            {
              (currentPage !== 1) && 
              <li><a onClick={() => pages(1)}>1</a></li>
            }
            {/* ...  */}
            {
              (currentPage !== 1) && 
              <li>...</li>
            }
            {/* previous number */}
            {
              (currentPage !== 1) && 
              <li><a onClick={() => pages(currentPage-1)}>{currentPage-1}</a></li>
            }
            {/* current */}
            <li><a onClick={() => pages(currentPage)}>{currentPage}</a></li>

            {/* next number */}
            {
              (currentPage !== lastPage) && 
              <li><a onClick={() => pages(currentPage+1)}>{currentPage+1}</a></li>
            }
            {/* ...  */}
            {
              (currentPage !== lastPage) && 
               <li>...</li>
            }

            {/* Last number */}
            {
              (currentPage !== lastPage) &&
               <li><a onClick={() => pages(pageNumbers.length)}>{pageNumbers.length}</a></li>
            }
            {/* next */}
            {
              (currentPage !== lastPage) &&
              <li><a onClick={() => pages(currentPage+1)}>Next</a></li>
            }

      </ul>
    </nav>
  );
}
