import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { difficultyLevels, durationLevels } from './ActivitiesLevels'
import SelectCountries from './SelectCountries';
import nameIco from '../icons/text_fields_black_24dp.svg'
import difficultyIco from '../icons/equalizer_black_24dp.svg'
import durationIco from '../icons/timelapse_black_24dp.svg'
import seasonIco from '../icons/date_range_black_24dp.svg'
import './Activities.css';

export default function Activities() {
    const responseText = useRef();
    // const difficultyLevels = ['Easy', 'Beginner', 'Average', 'Hard', 'Expert'];
    // const durationLevels = ['Less than 30 minutes', 'Less than an hour', 'A couple of hours', 'Half a day', 'Whole day', 'A few days', 'Less than a week', 'A few weeks']

    // FORM VALIDATION
    function validate(input) {
        let errors = {};

        if (!input.name) {
            errors.name = 'All fields are required';
        } else if (!(/^[a-zA-Z0-9!Ññ@#$%&*()_ ;:\-]*$/.test(input.name))) {
            errors.name = 'No special characters allowed'
        } else if (!(/^[a-zA-ZÑñ].*/.test(input.name))) {
            errors.name = 'Names cant begin with a number'
        }

        if (!input.season) { errors.season = 'All fields are required'; }

        return errors;
    };

    // FORM DATA
    const [errors, setErrors] = useState({});
    const [input, setInput] = useState({
        name: '',
        difficulty: '3',
        duration: '3',
        season: '',
        countries: [],
    });
    function handleInputChange(e) {
        const currentInputs = { ...input, [e.target.name]: e.target.value };
        setInput(currentInputs);
        setErrors(validate(currentInputs));
        console.log(input);
    }

    // DATA SUBMIT
    async function handleSubmit(e) {
        e.preventDefault();
        const response = await axios.post('http://localhost:3001/activities', input);
        responseText.current.innerHTML = (response.status === 200)
            ? " Activity *" + input.name + "* has been added"
            : response.status + " " + response.statusText;
    }

    // CHECKBOX
    const [selectCountries, setSelectCountries] = useState(false);
    function checkBoxHandler(e) {
        setSelectCountries(e.target.checked);
    }
    // SELECT COUNTRIES ACTIVITY
    const [countriesPerActivity, setCountriesPerActivity] = useState([]);
    const countriesActivity = (countries) => {
        setCountriesPerActivity(countries.filter(c => c.selected === true).map(c => c.id));
    };
    useEffect(() => {
        setInput({ ...input, countries: countriesPerActivity })
    }, [countriesPerActivity])

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <img src={nameIco} alt="DifLevel" />
                    <label>Name:</label>
                    <input type="text" name="name" value={input.name}
                        className={errors.name && 'danger'} // errors.username ? 'danger' : ''
                        onChange={handleInputChange} />
                    {errors.name && (
                        <span className='danger'>{errors.name}</span>
                    )}
                </div>
                <div>
                    <img src={difficultyIco} alt="DifLevel" />
                    <label>Difficulty:</label>
                    <input type="range" name="difficulty" min="1" max="5" value={input.difficulty}
                        className={errors.difficulty && 'danger'}
                        onChange={handleInputChange} />
                    <span>{difficultyLevels[input.difficulty - 1]}</span>
                    {errors.difficulty && (<span className='danger'>{errors.difficulty}</span>)}
                </div>
                <div>
                    <img src={durationIco} alt="Duration" />
                    <label>Duration:</label>
                    <input type="range" name="duration" min='1' max='8' value={input.duration}
                        className={errors.duration && 'danger'}
                        onChange={handleInputChange} />
                    <span>{durationLevels[input.duration - 1]}</span>
                    {errors.duration && (
                        <span className='danger'>{errors.duration}</span>
                    )}
                </div>
                <div>
                    <img src={seasonIco} alt="Season" />
                    <label>Season:</label>
                    <select name="season" onChange={handleInputChange} required>
                        <option value="" selected disabled hidden>Choose a season</option>
                        <option value='Spring'>Spring</option>
                        <option value='Summer'>Summer</option>
                        <option value='Autumn'>Autumn</option>
                        <option value='Winter'>Winter</option>
                    </select>
                    {errors.season && (<span className='danger'>{errors.season}</span>)}
                </div>

                <input type="submit" value="Submit" disabled={Object.keys(errors).length} />
                <span ref={responseText}></span>

                <div>
                    <label>Select countries ?</label>
                    <input type='checkbox' onChange={checkBoxHandler} />
                </div>
            </form>

            <div>
                {selectCountries && <SelectCountries countriesActivity={countriesActivity} />}
            </div>
        </div>
    )
}
