import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getCountryDetails } from "../actions";
import ActivityCard from "./ActivityCard";
import style from './CountryDetails.module.css';

export default function CountryDetails() {
    const dispatch = useDispatch();
    const { id } = useParams();

    const countryDetails = useSelector((state) => state.countryDetails);

    // InicializaciÃ³n
    useEffect(() => {
        dispatch(getCountryDetails(id));
    }, [])

    return (
        <div id={style.detail}>
            {
                countryDetails.length !== 0 ?
                    (<div id={style.countryDetail}>
                        {/* {countryDetails.flag_icon} */}
                        <div id={style.name}>{countryDetails.name}</div>
                        <div id={style.id}>({countryDetails.id})</div>
                        <img id={style.flag} src={countryDetails.flag} alt="flag not found" width="150px" />
                        <div id={style.data}>
                            <div className={style.dataRow}>
                                <span className={dataLabel}>Region : </span>
                                <span className={style.dataValue}>{countryDetails.region}-{countryDetails.subregion}</span></div>
                            <div className={style.dataRow}>
                                <span className={dataLabel}>Capital: </span>
                                <span className={style.dataValue}>{countryDetails.capital}</span></div>
                            <div className={style.dataRow}>
                                <span className={dataLabel}>Area: </span>
                                <span className={style.dataValue}>{
                                countryDetails.area > 1000000
                                    ? (Math.round((countryDetails.area / 1000000) * 100) / 100 + ' M Km2') // ROUND((area/millon)*digitos dec)/digitos dec = M Km2
                                    : Number(countryDetails.area).toLocaleString() + ' Km2'}</span></div>
                            <div className={style.dataRow}>
                                <span className={dataLabel}>Population: </span>
                                <span className={style.dataValue}>{Number(countryDetails.population).toLocaleString()}</span></div>
                        </div>
                        {
                            countryDetails.activities &&
                            countryDetails.activities.map(act => (
                                <ActivityCard
                                    name={act.name}
                                    difficulty={act.difficulty}
                                    duration={act.duration}
                                    season={act.season}
                                />
                            ))
                        }
                    </div>
                    ) : <div className={style.loader}></div>
            }
        </div>
    )
}
