import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from "react-redux";
import axios from 'axios';

import './Activities.css';
import SelectCountries from './SelectCountries';

export default function Activities() {
    const dispatch = useDispatch();
    const responseText = useRef();

    // FORM VALIDATION
    function validate(input) {
        let errors = {};
        if (!input.name) {
            errors.name = 'Activity name is required';
        } else if(!/[A-Za-z0-9\.,;:!?\- _Ã¡Ã©Ã­Ã³ÃºÃ±Ã‘]+/.test(input.name)){
            errors.name = 'Cannot use some special characters'
        }

        // if (!input.difficulty || input.difficulty === '') {
        //     console.log("ðŸš€ ~ file: Activities.jsx ~ line 11 ~ validate ~ input.difficuty", input.difficuty)
        //     errors.difficulty = 'Difficuty is required'
        //        } else if(!/(?=.*[0-9])/.test(input.difficuty)) {
        //            errors.difficuty = 'Difficuty is invalid'
        // }
        // console.log(errors);
        return errors;
    };

    // FORM DATA
    const [errors, setErrors] = useState({});
    const [input, setInput] = useState({
        name: '',
        difficulty: '',
        duration: '',
        season: '',
        countries: [],
    });
    function handleInputChange(e) {
        const currentInputs = { ...input, [e.target.name]: e.target.value };
        setInput(currentInputs);
        setErrors(validate(currentInputs));
    }

    // DATA SUBMIT
    async function handleSubmit(e) {
        e.preventDefault();
        const response = await axios.post('http://localhost:3001/activities', input);
        console.log("ðŸš€ ~ file: Activities.jsx ~ line 47 ~ handleSubmit ~ response", response)
        responseText.current.innerHTML = (response.status===200) 
                                                ? " Activity *" + input.name + "* has been added" 
                                                : response.status + " " + response.statusText;
    }
    // SELECT COUNTRIES
    const [selectedCountries, setSelectedCountries] = useState([]);
    const countriesActivity = (countries) => {
        setSelectedCountries(countries.filter(c => c.selected === true).map(c => c.id));
    };
    useEffect(() => {
        setInput({...input, countries: selectedCountries})
    }, [selectedCountries])

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" value={input.name}
                        className={errors.name && 'danger'} // errors.username ? 'danger' : ''
                        onChange={handleInputChange} />
                    {errors.name && (
                        <span className='danger'>{errors.name}</span>
                    )}
                </div>
                <div>
                    <label>Difficulty:</label>
                    <input type="text" name="difficulty" value={input.difficulty}
                        className={errors.difficulty && 'danger'} // errors.username ? 'danger' : ''
                        onChange={handleInputChange} />
                    {errors.difficulty && (
                        <span className='danger'>{errors.difficulty}</span>
                    )}
                </div>
                <div>
                    <label>Duration:</label>
                    <input type="text" name="duration" value={input.duration}
                        className={errors.duration && 'danger'} // errors.username ? 'danger' : ''
                        onChange={handleInputChange} />
                    {errors.duration && (
                        <span className='danger'>{errors.duration}</span>
                    )}
                </div>
                <div>
                    <label>Season:</label>
                    <input type="text" name="season" value={input.season}
                        className={errors.season && 'danger'} // errors.username ? 'danger' : ''
                        onChange={handleInputChange} />
                    {errors.season && (
                        <span className='danger'>{errors.season}</span>
                    )}
                </div>

                <input type="submit" value="Submit" disabled={Object.keys(errors).length} />
                <span ref={responseText}></span>
            </form>

            <div>
                <SelectCountries countriesActivity={countriesActivity} />
            </div>
        </div>
    )
}
