import React from "react";
import './Paging.css'

export default function Paging({ countriesXPage, totalCountries, pages }) {
  const pageNumbers = [];
  const totalPages = Math.ceil(totalCountries / countriesXPage);
  console.log("ðŸš€ ~ file: Paging.jsx ~ line 7 ~ Paging ~ totalPages", totalPages)

  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }
  const firstPage = 1;
  const lastPage = pageNumbers.length-1

  return (
    <nav>
      <ul>
            {/* Prev */}
            <li></li>
            {/* First number */}
            <li><a onClick={() => pages(1)}>1</a></li>
            {/* ...  */}
            <li>...</li>
            {/* previous number */}
            <li>4</li>
            {/* current */}
            <li>5</li>
            {/* next number */}
            <li>6</li>
            {/* ...  */}
            <li>...</li>
            {/* Last number */}
            <li><a onClick={() => pages(pageNumbers.length-1)}>{pageNumbers.length-1}</a></li>
            {/* next */}
            <li>Next</li>
      </ul>
    </nav>
  );
}
