import React from 'react'
import { useState, useEffect, } from "react";
import { useSelector, useDispatch } from "react-redux";


export default function SelectCountries() {
    const countries = useSelector(state => state.countries, () => { });

    const [selectedCountries, setSelectedCountries] = useState([]);
    const [regionList, setRegionList] = useState([]);

    useEffect(() => {
        if (selectedCountries.length === 0) {
            //// FILL REGION LIST
            setRegionList(countries
                .map((obj) => obj.region) // convierte el obj en arr
                .filter((item, index, arr) => arr.indexOf(item) === index)); // filtra repeticiones);
            //// FILL SELECTED COUNTRIES
            const arr = [];
            countries.forEach(c => {
                const obj = {
                    name: c.name,
                    region: c.region,
                    flag_icon: c.flag_icon,
                    selected: false
                };
                arr.push(obj);
            })
            arr.sort(function (a, b) {
                const A = a.name.toUpperCase();
                const B = b.name.toUpperCase();
                if (A < B) return -1;
                if (A > B) return 1;
                return 0;
            });
            setSelectedCountries(arr);
            // setSelectedCountries(countries.map(c => (
            //     {
            //         name: c.name,
            //         region: c.region,
            //         flag_icon: c.flag_icon,
            //         selected: false
            //     }
            // )))
        }
    }, [countries]);

    // useEffect(()=> {
    //     console.log("ðŸš€ ~ file: SelectCountries.jsx ~ line 51 ~ SelectCountries ~ regionList", regionList)
    // },[regionList])

    return (
        <div><ul>
            {
                selectedCountries &&
                selectedCountries.map(c =>
                (c.region = 'Asia' &&
                    (<li key={c.id}>
                        <input type='checkbox' />
                        {c.name}
                        {c.flag_icon}
                    </li>)
                )
                )
            }


        </ul></div>
    )
}
