import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, getActivitiesList } from "../actions";
import { Link } from "react-router-dom";
import CountryCard from "./CountryCard.jsx";

export default function Home() {
  const dispatch = useDispatch();
  const countries = useSelector((state) => state.countries);
  const activitiesList = useSelector((state) => state.activitiesList);

  useEffect(() => {
    dispatch(getCountries());
    dispatch(getActivitiesList());
    const uniqueRegions = regions();
    return () => {};
  }, [dispatch]);

  function regions() {
    return countries
    .map((obj) => obj.region) // convierte el obj en arr
    .filter((item, index, arr) => arr.indexOf(item) === index); // filtra repeticiones
    console.log("ðŸš€ ~ file: Home.jsx ~ line 22 ~ Home ~ uniqueRegions", uniqueRegions)
  }




  function handleReloadDB(e) {
    e.preventDefault();
    dispatch(getCountries());
  }
  // La barra de botones se podria modularizar

  // SEGUIR IMPLEMENTANDO BARRA
  return (
    <div>
      <Link to="/activity">Crear actividad</Link>
      <h1>Home de Countries</h1>
      <button
        onClick={(e) => {
          handleReloadDB(e);
        }}
      >
        reload DB
      </button>
      <div>
        <select name="orderBy" id="">
          <option value="asc">Ascendente</option>
          <option value="des">Descendente</option>
          <option value="des">PoblaciÃ³n</option>
        </select>

        <label>Search:</label>
        <input type="text" id="search" name="search"></input>
        <button>Serch</button>

        <select name="region" id=""></select>

        <select name="activity" id="">
          {activitiesList &&
            activitiesList.map((act) => {
              return <option value={act}>{act}</option>;
            })}
        </select>

        <div>
          {
            countries ? (
              countries.map((country) => {
                return (
                  <CountryCard
                    key={country.id}
                    name={country.name}
                    region={country.region}
                    flag={country.flag}
                  />
                );
              })
            ) : (
              <h2>No hay paises para mostrar</h2>
            )
            // countries && countries.map(country => {
            //     <CountryCard name={country.name} region={country.region} flag={country.flag} />
            // })
          }
        </div>
      </div>
    </div>
  );
}
