import React from 'react'
import { Link } from "react-router-dom";
import styles from './Nav.module.css';


export default function Nav({ countriesXPage, setCountriesXPage, handleReloadDB }) {
    return (
        <div className={styles.container}>
            <label>Results/page:</label>
            <input type="range" min='8' max='36' value={countriesXPage} onChange={(e) => setCountriesXPage(e.target.value)} />
            <span>{countriesXPage}</span>

            <Link to="/activities">
                <input type="button" value="Activities" className={styles.button} />
            </Link>

            <input type="button" value="Reload" onChange={handleReloadDB} className={styles.button} />
        </div>
    )
}
