import React, { useState } from 'react';
import './Activities.css';

export function validate(input) {
  let errors = {};
  if (!input.name) {
      errors.name = 'Activity name is required';
  } else if (!/\S+@\S+\.\S+/.test(input.name)) {
      errors.name = 'Activity name is invalid';
  }
  if (!input.difficuty || input.difficuty === '') {
      errors.difficuty = 'Difficuty is required'
  } else if(!/(?=.*[0-9])/.test(input.difficuty)) {
      errors.difficuty = 'Difficuty is invalid'
  }
  // console.log(errors);
  return errors;
};

function Activities() {

  const [input, setInput] = useState({
    name: '',
    difficulty: '',
    duration: '',
    season: '',
  });
  const [errors, setErrors] = useState({});

  function handleInputChange(e) {
    const currentInputs = {...input,[e.target.name]: e.target.value,};
    setInput(currentInputs);
    setErrors(validate(currentInputs));
  }

  return (
    <div>
      <form>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={input.name}
            className={errors.name && 'danger'} // errors.username ? 'danger' : ''
            onChange={handleInputChange} />
          {errors.name && (
            <p className='danger'>{errors.name}</p>
          )}
        </div>
        <div>
          <label>Difficulty:</label>
          <input type="text" name="difficulty" value={input.difficulty}
            className={errors.difficulty && 'danger'} // errors.username ? 'danger' : ''
            onChange={handleInputChange} />
          {errors.difficulty && (
            <p className='danger'>{errors.difficulty}</p>
          )}
        </div>
        <div>
          <label>Duration:</label>
          <input type="text" name="duration" value={input.duration}
            className={errors.duration && 'danger'} // errors.username ? 'danger' : ''
            onChange={handleInputChange} />
          {errors.duration && (
            <p className='danger'>{errors.duration}</p>
          )}
        </div>
        <div>
          <label>Season:</label>
          <input type="text" name="season" value={input.season}
            className={errors.season && 'danger'} // errors.username ? 'danger' : ''
            onChange={handleInputChange} />
          {errors.season && (
            <p className='danger'>{errors.season}</p>
          )}
        </div>

        <input type="submit" value="Submit" disabled = {Object.keys(errors).length}/>
      </form>
    </div>
  )
}

export default Activities;
