import React from "react";
import './Paging.css'

export default function Paging({ totalPages, currentPage, pages }) {
  const pageNumbers = [];

  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }
  const firstPage = 1;
  const lastPage = totalPages;

  return (
    <nav>
      <ul>
        {/* Prev */}
        {/* <li id={currentPage !== firstPage ? 'show' : 'hide'}><button onClick={() => pages(currentPage-1)}>Prev</button></li> */}

        {/* First number */}
        <li id={currentPage !== firstPage ? 'show' : 'hide'}><button id='first' onClick={() => pages(firstPage)}>{firstPage}</button></li>

        {/* ...  */}
        <li id={currentPage !== firstPage ? 'show' : 'hide'}>---</li>

        {/* previous number */}
        <li id={currentPage !== firstPage ? 'show' : 'hide'}><button onClick={() => pages(currentPage - 1)}>{currentPage - 1}</button></li>

        {/* current */}
        <li><button id='current'>{currentPage}</button></li>

        {/* next number */}
        <li id={currentPage !== lastPage ? 'show' : 'hide'}><button onClick={() => pages(currentPage + 1)}>{currentPage + 1}</button></li>

        {/* ...  */}
        <li id={currentPage !== lastPage ? 'show' : 'hide'}>...</li>

        {/* Last number */}
        <li id={currentPage !== lastPage ? 'show' : 'hide'}><button id='last' onClick={() => pages(lastPage)}>{lastPage}</button></li>

        {/* next */}
        {/* <li id={currentPage !== lastPage ? 'show' : 'hide'}><button onClick={() => pages(currentPage+1)}>Next</button></li> */}
      </ul>
    </nav>
  );
}
